/* eslint-disable no-unused-vars */
import { createElement as $, useState, useEffect } from 'react'
import { toast } from 'react-toastify'
import { useSelector } from 'react-redux'
import {
  createCategory,
  getCategories,
  deleteCategory,
} from '../../../functions/category'
import AdminNav from '../../../components/nav/AdminNav'

const CategoryCreate = () => {
  // State for form control
  const [name, setName] = useState('')
  const [loading, setLoading] = useState(false)
  const [categories, setCategories] = useState([''])

  // Retrieve admin token
  const { user } = useSelector((state) => ({ ...state }))

  // Function to load categories and save it into the state
  const loadCategories = () => {
    getCategories().then((res) => console.log(res.data))
  }

  useEffect(() => {
    loadCategories()
  }, [])

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    createCategory({ name }, user.token)
      .then((res) => {
        setLoading(false)
        setName('')
        toast.success(`"${name}" is created`)
      })
      .catch((err) => {
        setLoading(false)
        console.log(err.response)
        if (err.response.status === 400) toast.error(err.response.data.name)
        setName('')
      })
  }
  return $(
    'div',
    { className: 'container-fluid' },
    $(
      'div',
      { className: 'row' },
      $('div', { className: 'col-md-2' }, $(AdminNav)),
      $(
        'div',
        { className: 'col' },
        loading
          ? $('h4', { className: 'text-danger' }, 'Loading ...')
          : $('h4', null, 'Create Category'),
        $(
          'form',
          { onSubmit: handleSubmit },
          $(
            'div',
            { className: 'form-group' },
            $('label', null, 'Name'),
            $('input', {
              className: 'form-control',
              type: 'text',
              onChange: (e) => setName(e.target.value),
              value: name,
              autoFocus: true,
              required: true,
            }),
            $('br'),
            $('button', { className: 'btn btn-outline-primary' }, 'Save'),
          ),
        ),
      ),
    ),
  )
}
export default CategoryCreate
